function init (vehicles, cities, passengers, game) {
	let v = vehicles[0];
	/*	The class Vehicle has some events that
		can help you out while moving your
		vehicles from city to city.
		
		The "visitCity" event is triggered
		every time the vehicle visits a city.
	*/
	
	v.on('idle', function(city) {
		v.moveTo(['B','C','D','C','A']);
	});
}
