/**
 The function init runs when the game starts
 and recieves three parameters:
 
 vehicles: array of Vehicle in this challenge
 cities: array of City in this challenge
 game: Game functions for calculating paths
 
 You can learn more about the game in the docs
 or just by looking below on the short docs
*/

function init (vehicles, cities, game) {
	let v = vehicles[0];
	/*	The class Vehicle has some events that
		can help you out while moving your
		vehicles from city to city.
		
		The "visitCity" event is triggered
		every time the vehicle visits a city.
	*/
	
	v.on('idle', function(city) {
		v.moveTo(['B','C','D','C','A']);
	});
}
